@article{livingston2019culture,
    author = {J. Livingston},
    title = {Culture Conditions Influence Iridescence in Marine Microbes},
    journal = {Caltech Undergraduate Research Journal},
    year = {2019},
    month = {08},
    url = {https://curj.caltech.edu/2019/08/27/culture-conditions-influence-iridescence-in-marine-microbes/},
    abstract = {This study investigates the effect of culture conditions on iridescence in marine bacteria, specifically within the genus Tenacibaculum. By isolating and classifying several new strains of iridescent Tenacibaculum, we characterized their iridescence under various culture conditions, including pH, agar concentration, salinity, and nutrient availability. The findings suggest that iridescence is influenced by the rigidity of the growth surface and that specific environmental factors may play a role in the biological significance of this phenomenon.}
}
