@article{Urbonavicius2007,
    author = {Urbonavicius, Jaunius and Brochier-Armanet, Céline and Skouloubris, Stéphane and Myllykallio, Hannu and Grosjean, Henri},
    title = {In vitro detection of the enzymatic activity of folate-dependent tRNA (Uracil-54,-C5)-methyltransferase: evolutionary implications},
    journal = {Methods in Enzymology},
    volume = {425},
    pages = {103-119},
    year = {2007},
    abstract = {Formation of 5-methyluridine (ribothymidine) at position 54 of the T-psi loop of tRNA is catalyzed by site-specific tRNA methyltransferases (tRNA[uracil-54,C5]-MTases). In eukaryotes and many bacteria, the methyl donor for this reaction is generally S-adenosyl-L-methionine (S-AdoMet). However, in other bacteria, like Enterococcus faecalis and Bacillus subtilis, it was shown that the source of carbon is N(5),N(10)-methylenetetrahydrofolate (CH(2)=THF). Recently we have determined that the Bacillus subtilis gid gene (later renamed to trmFO) encodes the folate-dependent tRNA(uracil-54,C5)-MTase. Here, we describe a procedure for overexpression and purification of this recombinant enzyme, as well as detection of its activity in vitro. Inspection of presently available sequenced genomes reveals that trmFO gene is present in most Firmicutes, in all alpha- and delta-Proteobacteria (except Rickettsiales in which the trmFO gene is missing), Deinococci, Cyanobacteria, Fusobacteria, Thermotogales, Acidobacteria, and in one Actinobacterium. Interestingly, trmFO is never found in genomes containing the gene trmA coding for S-adenosyl-L-methionine-dependent tRNA (uracil-54,C5)-MTase. The phylogenetic analysis of TrmFO sequences suggests an ancient origin of this enzyme in bacteria.},
    url = {https://difusion.ulb.ac.be/vufind/Record/ULB-DIPOT:oai:dipot.ulb.ac.be:2013/177338/TOC}
}
